const N = 10000;
function generateData(points = 5000) {
    const data = [];

    let y = 0;
    for (let i = 0; i < points; i++) {
        y += Math.random() - 0.5;
        data.push({ x: i + 1, y });
    }

    return data;
}

msg.payload = {
    type: 'line',
    data: {
        datasets: [{
            label: 'Decimation probe',
            data: generateData(N),
            pointRadius: 0,
            borderWidth: 1
        }]
    },
    options: {
        parsing: false,
        plugins: {
            title: { display: true, text: 'Decimation probe (should drop length if active)' },
            decimation: {
                enabled: true,
                algorithm: 'lttb',
                threshold: 1000,
                samples: 250
            },
        },
        scales: { x: { type: 'linear' } }
    }
};

return msg;

// This plugin counts the data points before and after data decimation

const probePlugin = {
    id: 'probePointCount',
    afterDatasetsDraw(chart) {
        const ds = chart.data?.datasets?.[0];
        const len = ds?.data?.length ?? 0;
        const origLen = ds?._data?.length; // decimation plugin stores original here if it ran

        const ctx = chart.ctx;
        ctx.save();
        ctx.font = '12px sans-serif';
        ctx.textBaseline = 'top';

        // draw a white-ish background box for readability
        const text1 = `data.length: ${origLen ?? 'n/a'}`;
        const text2 = `after decimation: ${len}`;
        const pad = 6;
        const w = Math.max(ctx.measureText(text1).width, ctx.measureText(text2).width) + pad * 2;
        const h = 16 * 2 + pad * 3;

        ctx.fillStyle = 'rgba(255,255,255,0.85)';
        ctx.fillRect(10, 10, w, h);

        ctx.fillStyle = '#111';
        ctx.fillText(text1, 10 + pad, 10 + pad);
        ctx.fillText(text2, 10 + pad, 10 + pad + 16 + pad);

        ctx.restore();
    }
};

msg.plugins = [probePlugin];

return msg;

